import React from "react";

const BackgroundSelector = ({ onBackgroundSelect, selectedBackground }) => {
  const backgrounds = [
    { id: "bg1", name: "Background 1", img: "/assets/bg1.jpg" },
    { id: "bg2", name: "Background 2", img: "/assets/bg2.jpg" },
    { id: "bg3", name: "Background 3", img: "/assets/bg3.jpg" },
  ];

  return (
    <div className="background-selector">
      <h3>Select Background</h3>
      <div className="background-buttons">
        {backgrounds.map((bg) => (
          <button
            key={bg.id}
            className={`background-btn ${selectedBackground === bg.id ? "selected" : ""}`}
            onClick={() => onBackgroundSelect(bg.id)}
          >
            <img src={bg.img} alt={bg.name} />
          </button>
        ))}
      </div>
    </div>
  );
};

export default BackgroundSelector;
