import React from "react";

const SimulatorDisplay = ({ simulatorType, selectedMarbles, background }) => {
  const marbleImages = {
    marble1: [
      "/assets/marble1-click1.jpg",
      "/assets/marble1-click2.jpg",
      "/assets/marble1-click3.jpg",
    ],
    marble2: [
      "/assets/marble2-click1.jpg",
      "/assets/marble2-click2.jpg",
      "/assets/marble2-click3.jpg",
    ],
    marble3: [
      "/assets/marble3-click1.jpg",
      "/assets/marble3-click2.jpg",
      "/assets/marble3-click3.jpg",
    ],
  };

  const backgroundImages = {
    bg1: "../assets/images/backgrounds/2-M1-CREAM-KW.webp",
    bg2: "/assets/bg2.jpg",
    bg3: "/assets/bg3.jpg",
  };

  return (
    <div className="simulator-display">
      <h3>Simulation Display</h3>
      <div className="display-area">
        {/* Render marbles in order */}
        {selectedMarbles.map((marbleId, index) => (
          <img
            key={index}
            src={marbleImages[marbleId][index]} // Dynamically get the marble image based on click index
            alt={`Marble ${index + 1}`}
            className={`marble-layer click-${index + 1}`}
          />
        ))}

        {/* Render background */}
        {background && (
          <img
            src={backgroundImages[background]}
            alt="Selected Background"
            className="background-layer"
          />
        )}
      </div>
    </div>
  );
};

export default SimulatorDisplay;
